import React from 'react';
import {
  FaTh,
  FaUserAlt,
  FaThList
}from "react-icons/fa";
// import { NavLink } from 'react-router-dom';
import { Sidebar, Menu, MenuItem } from 'react-pro-sidebar';
import { Link } from 'react-router-dom';

const NavAdmin = () => {
  const menuItem = [
    {
        path:"/dashboard",
        name:"Dashboard",
        icon:<FaTh/>
    },
    {
        path:"/dashboard/tabelkegiatan",
        name:"Tabel Kegiatan",
        icon:<FaThList/>
    },
    {
        path:"/dashboard/nilai",
        name:"Nilai",
        icon:<FaUserAlt/>
    }
  ]

  return (
    <div style={{ display: 'flex', flexDirection: "row", height: '100%', minHeight: '750px' }}>
      <Sidebar transitionDuration={1000} backgroundColor="#434242">
        <Menu menuItemStyles={{
          button: ({ level, disabled }) => {
            // only apply styles on first level elements of the tree
            if (level === 0)
              return {
                color: disabled ? '#ffffff' : '#ffffff',
              };
          },
        }}>
          {
            menuItem.map((item) => (
              <MenuItem as={Link} to={item.path} key={item.name}>
                <div className="list_item">{item.name}</div>
              </MenuItem>
            ))
          }
        </Menu>
      </Sidebar>
      <div>
      tes
    </div>
    </div>
    
    // <div className="container">
    //        <div style={{width: isOpen ? "200px" : "50px"}} className="sidebar">
    //            <div className="top_section">
    //                <h1 style={{display: isOpen ? "block" : "none"}} className="logo">Logo</h1>
    //                <div style={{marginLeft: isOpen ? "50px" : "0px"}} className="bars">
    //                    <FaBars onClick={toggle}/>
    //                </div>
    //            </div>
    //            {
    //                menuItem.map((item, index)=>(
    //                    <NavLink to={item.path} key={index} className="link" activeclassName="active">
    //                        <div className="icon">{item.icon}</div>
    //                        <div style={{display: isOpen ? "block" : "none"}} className="link_text">{item.name}</div>
    //                    </NavLink>
    //                ))
    //            }
    //        </div>
    //        <main>{children}</main>
    // </div>
  )
};

export default NavAdmin;
